# frozen_string_literal: true

require 'spec_helper'
require 'json'

describe Parser::Google::ImageCarousel do
  let(:html) do
    File.open('./files/van-gogh-paintings.html')
  end
  let(:parser) { described_class.new html }

  describe '#to_hash' do
    it 'returns artworks data from parsed page' do
      artworks = parser.to_hash[:artworks]
      artwork_data = {
        name: 'The Starry Night',
        extensions: ['1889'],
        link: 'https://www.google.com/search?gl=us&hl=en&q=The+Starry+Night&stick=H4sIAAAAAAAAAONgFuLQz9U3MI_PNVLiBLFMzC3jC7WUspOt9Msyi0sTc-ITi0qQmJnFJVbl-UXZxY8YI7kFXv64JywVMGnNyWuMXlxEaBJS4WJzzSvJLKkUkuLikYLbrcEgxcUF5_EsYhUIyUhVCC5JLCqqVPDLTM8oAQDmNFnDqgAAAA&npsic=0&sa=X&ved=0ahUKEwiL2_Hon4_hAhXNZt4KHTOAACwQ-BYILw',
        image: 'data:image/jpeg;base64,'
      }

      expect(artworks).to include artwork_data
    end

    context 'when html input is empty' do
      let(:html) { '' }
      it 'returns empty nokogiri document' do
        empty_data = {
          artworks: []
        }
        expect(parser.to_hash).to eq empty_data
      end
    end
  end

  describe '#carousel' do
    it 'returns carousel element from defined page' do
      expect(parser.carousel.elements).not_to be_empty
    end

    context 'when carousel element does not exists' do
      let(:html) { '' }
      it 'returns empty array' do
        expect(parser.carousel).to be_nil
      end
    end
  end

  describe '#image_base64' do
    it 'returns hash of base64 image from js function' do
      base64_data = {
        'dimg_85' => 'data:image/jpeg;base64,'
      }
      expect(parser.send(:image_base64)).to include base64_data
    end

    context 'when js function not available' do
      let(:html) { '' }
      it 'returns empty hash' do
        expect(parser.send(:image_base64)).to be_empty
      end
    end
  end
end
